mapper.select:
SELECT t.task_id AS taskId, t.milestone_id AS milestoneId, u.user_name AS userName, t.task_category AS taskCategory, t.task_name AS taskName, t.task_description AS taskDescription, t.task_status AS taskStatus, t.created_at AS createdAt, t.updated_at AS updatedAt, t.start_date AS startDate, t.end_date AS endDate, t.is_completed AS isCompleted, t.is_deleted AS isDeleted FROM task t JOIN milestone m ON m.milestone_id = t.milestone_id LEFT JOIN users u ON u.user_id = t.user_id WHERE m.project_id = #{projectId} AND t.is_deleted = false
AND u.user_name IN
#{name}
AND t.task_status = #{status}
AND t.start_date >= #{startDate}
AND t.end_date <= #{endDate}
AND t.task_category IN
#{category}
ORDER BY t.start_date ASC, t.task_id ASC
